class YarRockFM{constructor(){this.news="",this.playlists="",this.songs=""}get_tree(){return fetch("https://api.github.com/repos/oldventura/yarrock-fm/git/trees/main",{method:"GET"}).then((t=>t.json())).then((t=>{this.news=t.tree[2].url,this.playlists=t.tree[3].url,this.songs=t.tree[4].url}))}get_news_pages(){return fetch(this.news,{method:"GET"}).then((t=>t.json())).then((t=>t.tree))}get_news(t){return fetch("https://raw.githubusercontent.com/oldventura/yarrock-fm/main/news/"+t+".json",{method:"GET"}).then((t=>t.json()))}get_playlist_pages(){return fetch(this.news,{method:"GET"}).then((t=>t.json())).then((t=>t.tree))}get_playlist(t){return fetch("https://raw.githubusercontent.com/oldventura/yarrock-fm/main/playlists/"+t+".json",{method:"GET"}).then((t=>t.json()))}get_songs(t){return fetch("https://raw.githubusercontent.com/oldventura/yarrock-fm/main/songs/"+t,{method:"GET"}).then((t=>t.text()))}get_list_url(t){return fetch(t,{method:"GET",mode:"no-cors"})}}